-- Table structure for blog_records
-- ----------------------------
DROP TABLE IF EXISTS `blog_records`;
CREATE TABLE `blog_records` (
  `id` int(5) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `reccont` text COMMENT '说说内容',
  `rectime` int(10) DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='说说';

