-- ----------------------------
-- Table structure for blog_resources
-- ----------------------------
DROP TABLE IF EXISTS `blog_resources`;
CREATE TABLE `blog_resources` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `resname` varchar(60) DEFAULT NULL COMMENT '分享标题',
  `resdesc` text COMMENT '简介',
  `resimg` text COMMENT '分享缩略图',
  `resadd` text COMMENT '分享地址',
  `clicks` int(10) DEFAULT '0' COMMENT '点击数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='资源分享';

